import React, { useState, useEffect } from 'react';
import Card from '../components/Card';
import Carousel from '../components/Carousel';
import Footer from '../components/Footer';
import Navbar from '../components/Navbar';

export default function Home() {
  const [foodCat, setFoodCat] = useState([]);
  const [foodItems, setFoodItems] = useState([]);
  const [search, setSearch] = useState('');
  const [loading, setLoading] = useState(true);

  const loadFoodItems = async () => {
    setLoading(true);
    try {
      let response = await fetch("http://localhost:5000/api/foodData", {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      let data = await response.json();
      if (Array.isArray(data) && data.length === 2) {
        setFoodItems(data[0] || []);
        setFoodCat(data[1] || []);
      } else {
        console.error("Unexpected response format:", data);
      }
    } catch (error) {
      console.error("Error loading food items:", error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadFoodItems();
  }, []);

  // Filter food items based on search term
  const filteredFoodItems = foodItems.filter(item =>
    item.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div>
      <Navbar />
      <Carousel />
      <div className='container'>
        <div className='mb-3'>
          <input
            type='text'
            className='form-control'
            placeholder='Search for food items...'
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
        </div>
        {loading ? (
          <div>Loading...</div>
        ) : foodCat.length > 0 ? (
          foodCat.map((data) => (
            <div className='row mb-3' key={data.id}>
              <div className='fs-3 m-3'>
                {data.CategoryName}
              </div>
              <hr id="hr-success" style={{ height: "4px", backgroundImage: "-webkit-linear-gradient(left,rgb(0, 255, 137),rgb(0, 0, 0))" }} />
              {filteredFoodItems.length > 0 ? filteredFoodItems.filter(
                (item) => item.CategoryName === data.CategoryName
              ).map(filterItems => (
                <div key={filterItems.id} className='col-12 col-md-6 col-lg-3'>
                  <Card foodName={filterItems.name} item={filterItems} options={filterItems.options[0]} ImgSrc={filterItems.img} />
                </div>
              )) : <div>No Such Data</div>}
            </div>
          ))
        ) : (
          <div>No Categories Available</div>
        )}
      </div>
      <Footer />
    </div>
  );
}
