import React, { useEffect, useState } from 'react';

export default function Footer() {
  <footer className="bg-dark text-light py-5">
  <div className="container">
    {/* Your existing Footer content */}
    {/* Footer Note */}
    <div className="text-center mt-5">
      <span className="text-muted">© 2024 GoFood, Inc</span>
    </div>
  </div>
</footer>
  const [reviews, setReviews] = useState([]);
  const [formData, setFormData] = useState({
    userId: '',
    foodItemId: '',
    rating: '',
    review: '',
  });
  const [editMode, setEditMode] = useState(false);
  const [currentReviewId, setCurrentReviewId] = useState(null);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState({ type: '', text: '' });

  const apiUrl = 'http://localhost:5000/api/reviews';

  // Fetch all reviews
  const fetchReviews = async () => {
    setLoading(true);
    try {
      const response = await fetch(apiUrl);
      if (!response.ok) {
        throw new Error(`Error: ${response.status} ${response.statusText}`);
      }
      const data = await response.json();
      setReviews(data);
    } catch (error) {
      setMessage({ type: 'danger', text: error.message });
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchReviews();
  }, []);

  // Handle input change
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Validate form data
  const validateForm = () => {
    const { userId, foodItemId, rating, review } = formData;
    if (!userId || !foodItemId || !rating || !review) {
      setMessage({ type: 'warning', text: 'Please fill in all fields.' });
      return false;
    }
    if (rating < 1 || rating > 5) {
      setMessage({ type: 'warning', text: 'Rating must be between 1 and 5.' });
      return false;
    }
    return true;
  };

  // Reset form
  const resetForm = () => {
    setFormData({
      userId: '',
      foodItemId: '',
      rating: '',
      review: '',
    });
    setEditMode(false);
    setCurrentReviewId(null);
  };

  // Create a new review
  const handleCreateReview = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;
    setLoading(true);
    try {
      const response = await fetch(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });
      if (!response.ok) {
        throw new Error('Failed to create review.');
      }
      const newReview = await response.json();
      setReviews([...reviews, newReview]);
      resetForm();
      setMessage({ type: 'success', text: 'Review added successfully.' });
    } catch (error) {
      setMessage({ type: 'danger', text: error.message });
    } finally {
      setLoading(false);
    }
  };

  // Update an existing review
  const handleUpdateReview = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;
    setLoading(true);
    try {
      const response = await fetch(`${apiUrl}/${currentReviewId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });
      if (!response.ok) {
        throw new Error('Failed to update review.');
      }
      const updatedReview = await response.json();
      setReviews(
        reviews.map((review) =>
          review._id === currentReviewId ? updatedReview : review
        )
      );
      resetForm();
      setMessage({ type: 'success', text: 'Review updated successfully.' });
    } catch (error) {
      setMessage({ type: 'danger', text: error.message });
    } finally {
      setLoading(false);
    }
  };

  // Delete a review
  const handleDeleteReview = async (id) => {
    if (!window.confirm('Are you sure you want to delete this review?')) return;
    setLoading(true);
    try {
      const response = await fetch(`${apiUrl}/${id}`, {
        method: 'DELETE',
      });
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to delete review.');
      }
      setReviews(reviews.filter((review) => review._id !== id));
      setMessage({ type: 'success', text: 'Review deleted successfully.' });
    } catch (error) {
      console.error('Error deleting review:', error); // Log the full error for debugging
      setMessage({ type: 'danger', text: error.message });
    } finally {
      setLoading(false);
    }
  };

  // Set review data for editing
  const handleEditReview = (review) => {
    setEditMode(true);
    setCurrentReviewId(review._id);
    setFormData({
      userId: review.userId || '',
      foodItemId: review.foodItemId || '',
      rating: review.rating || '',
      review: review.review || '',
    });
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  return (
    <footer className="bg-dark text-light py-5">
      <div className="container">
        <h2 className="mb-4">User Reviews</h2>

        {/* Alert Message */}
        {message.text && (
          <div className={`alert alert-${message.type}`} role="alert">
            {message.text}
          </div>
        )}

        {/* Review Form */}
        <form
          onSubmit={editMode ? handleUpdateReview : handleCreateReview}
          className="mb-5"
        >
          <div className="row g-3">
            <div className="col-md-3">
              <label htmlFor="userId" className="form-label">
                User ID
              </label>
              <input
                type="text"
                className="form-control"
                id="userId"
                name="userId"
                value={formData.userId}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className="col-md-3">
              <label htmlFor="foodItemId" className="form-label">
                Food Item ID
              </label>
              <input
                type="text"
                className="form-control"
                id="foodItemId"
                name="foodItemId"
                value={formData.foodItemId}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className="col-md-2">
              <label htmlFor="rating" className="form-label">
                Rating
              </label>
              <input
                type="number"
                className="form-control"
                id="rating"
                name="rating"
                min="1"
                max="5"
                value={formData.rating}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className="col-md-4">
              <label htmlFor="review" className="form-label">
                Review
              </label>
              <textarea
                className="form-control"
                id="review"
                name="review"
                rows="1"
                value={formData.review}
                onChange={handleInputChange}
                required
              ></textarea>
            </div>
          </div>
          <div className="mt-3">
            <button
              type="submit"
              className={`btn ${editMode ? 'btn-warning' : 'btn-success'}`}
              disabled={loading}
            >
              {loading
                ? 'Processing...'
                : editMode
                ? 'Update Review'
                : 'Submit Review'}
            </button>
            {editMode && (
              <button
                type="button"
                className="btn btn-secondary ms-2"
                onClick={resetForm}
                disabled={loading}
              >
                Cancel
              </button>
            )}
          </div>
        </form>

        {/* Reviews List */}
        <div>
          {loading ? (
            <div className="text-center">
              <div
                className="spinner-border text-light"
                role="status"
              ></div>
              <span className="ms-2">Loading Reviews...</span>
            </div>
          ) : reviews.length > 0 ? (
            <div className="row">
              {reviews.map((review) => (
                <div className="col-md-6 mb-4" key={review._id}>
                  <div className="card bg-secondary text-light">
                    <div className="card-body">
                      <h5 className="card-title">
                        User: {review.userId} | Food Item: {review.foodItemId}
                      </h5>
                      <h6 className="card-subtitle mb-2 text-warning">
                        Rating: {review.rating}/5
                      </h6>
                      <p className="card-text">{review.review}</p>
                      <button
                        className="btn btn-primary me-2"
                        onClick={() => handleEditReview(review)}
                      >
                        Edit
                      </button>
                      <button
                        className="btn btn-danger"
                        onClick={() => handleDeleteReview(review._id)}
                      >
                        Delete
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <p>No reviews available.</p>
          )}
        </div>

        {/* Footer Note */}
        <div className="text-center mt-5">
