import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from "react-router-dom";
import Badge from "react-bootstrap/Badge"; // Ensure this is the correct import
import Modal from '../Modal'; // Ensure you have a Modal component
import Cart from '../sc/Cart'; // Ensure you have the Cart component

export default function Navbar(props) {
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [cartItems, setCartItems] = useState([]); // Initialize with an empty array
    const [cartView, setCartView] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        setIsAuthenticated(!!localStorage.getItem("authToken"));
        // Fetch cart items from local storage or API if needed
        const storedCartItems = JSON.parse(localStorage.getItem("cartItems")) || [];
        setCartItems(storedCartItems);
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('authToken');
        localStorage.removeItem('cartItems'); // Clear cart items on logout if desired
        setIsAuthenticated(false);
        navigate("/signup");
    };

    return (
        <div>
            <nav className="navbar navbar-expand-lg navbar-dark bg-success position-sticky"
                style={{ boxShadow: "0px 10px 20px black", position: "fixed", zIndex: "10", width: "100%" }}>
                <div className="container-fluid">
                    <Link className="navbar-brand fs-1 fst-italic" to="/">The Lusty Spoon</Link>
                    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className="collapse navbar-collapse" id="navbarSupportedContent">
                        <ul className="navbar-nav me-auto mb-2 mb-lg-0">
                            <li className="nav-item">
                                <Link className="nav-link fs-5 mx-3 active" aria-current="page" to="/">Home</Link>
                            </li>
                            {isAuthenticated && (
                                <li className="nav-item">
                                    <Link className="nav-link fs-5 mx-3 active" aria-current="page" to="/myorder">My Orders</Link>
                                </li>
                            )}
                        </ul>
                        {!isAuthenticated ? (
                            <div className="d-flex">
                                <Link className="btn bg-white text-success mx-1" to="/login">Login</Link>
                                <Link className="btn bg-white text-success mx-1" to="/signup">Signup</Link>
                            </div>
                        ) : (
                            <div className="d-flex align-items-center">
                                <div className='btn bg-white text-success mx-2' onClick={() => setCartView(true)}>
                                    My Cart <Badge pill bg="danger">{cartItems.length}</Badge>
                                </div>
                                {cartView && (
                                    <Modal onClose={() => setCartView(false)}>
                                        <Cart cartItems={cartItems} />
                                    </Modal>
                                )}
                                <button onClick={handleLogout} className="btn bg-white text-success mx-2">Logout</button>
                            </div>
                        )}
                    </div>
                </div>
            </nav>
        </div>
    );
}
