import React, { useEffect, useState } from 'react';
import Footer from '../components/Footer';
import Navbar from '../components/Navbar';

export default function MyOrder() {
    const [orderData, setOrderData] = useState([]);

    const fetchMyOrder = async () => {
        const userEmail = localStorage.getItem('userEmail');
        console.log("Fetching order data for email:", userEmail);

        const response = await fetch("http://localhost:5000/api/myOrderData", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ email: userEmail })
        });

        const data = await response.json();
        console.log("Order data received:", data);

        // Check if order data exists and set it
        if (data.orderData && data.orderData.order_data) {
            setOrderData(data.orderData.order_data);
        } else {
            console.log("No order data available");
        }
    };

    useEffect(() => {
        fetchMyOrder();
    }, []);

    // Function to calculate the grand total for a single order
    const calculateGrandTotal = (items) => {
        return items.reduce((total, arrayData) => total + (arrayData.price * arrayData.qty), 0);
    };

    return (
        <div className="bg-dark text-light">
            <Navbar />
            <div className="container">
                <div className="row">
                    {orderData.length > 0 ? (
                        orderData.map((order, index) => (
                            <div key={index} className="mb-4">
                                <div className='m-auto mt-5'>
                                    {order[0]?.Order_date && (
                                        <h5 className="text-light">{order[0].Order_date}</h5>
                                    )}
                                    <hr />
                                </div>
                                {order.map((arrayData, idx) => (
                                    <div key={idx} className='col-12 col-md-6 col-lg-3'>
                                        <div className="card mt-3 bg-secondary">
                                            <div className="card-body">
                                                <h5 className="card-title">{arrayData.name}</h5>
                                                <div className='container w-100 p-0' style={{ height: "38px" }}>
                                                    <span className='m-1'>{arrayData.qty}</span>
                                                    <span className='m-1'>{arrayData.size}</span>
                                                    <div className='d-inline ms-2 h-100 w-20 fs-5'>
                                                        ₹{arrayData.price}/-
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                ))}
                                {/* Calculate and display the grand total */}
                                <div className="mt-3 fs-5 text-light">
                                    <strong>Grand Total: ₹{calculateGrandTotal(order)}</strong>
                                </div>
                            </div>
                        ))
                    ) : (
                        <div className="text-center m-5">No Orders Found</div>
                    )}
                </div>
            </div>
          
        </div>
    );
}
