import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useDispatchCart } from './ContextReducer';
import { useCart } from './ContextReducer';

export default function Card(props) {
  const dispatch = useDispatchCart();
  const navigate = useNavigate();
  const [qty, setQty] = useState(1);
  const [size, setSize] = useState(""); // Initialize size state
  const priceRef = useRef();
  const data = useCart();
  const options = props.options;
  const priceOptions = Object.keys(options);

  useEffect(() => {
    // Set default size to the first option in priceOptions
    if (priceOptions.length > 0) {
      setSize(priceOptions[0]);
    }
  }, [priceOptions]);

  const handleQty = (e) => {
    setQty(e.target.value);
  };

  const handleOptions = (e) => {
    setSize(e.target.value); // Update size when user selects an option
  };

  const handleAddtoCart = async () => {
    const finalPrice = qty * parseInt(options[size] || 0); // Recalculate inside handleAddToCart
    await dispatch({
      type: "ADD",
      id: props.foodItem._id,
      name: props.foodItem.name,
      price: finalPrice,
      qty: qty,
      size: size
    });
    console.log(data);
  };

  // Calculate final price outside, but ensure size is valid
  const finalPrice = qty * parseInt(options[size] || 0);

  return (
    <div>
      <div className="card mt-3 bg-dark text-light" style={{ width: "16rem", maxHeight: "360px" }}>
        <img src={props.foodItem.img} className="card-img-top" alt="..." style={{ height: "120px", objectFit: "fill" }} />
        <div className="card-body">
          <h5 className="card-title">{props.foodItem.name}</h5>
          <div className='container w-100 p-0' style={{ height: "38px" }}>
            <select className="m-2 h-100 w-20 bg-success text-white rounded" onChange={handleQty}>
              {Array.from(Array(6), (e, i) => (
                <option key={i + 1} value={i + 1}>{i + 1}</option>
              ))}
            </select>
            <select className="m-2 h-100 w-20 bg-success text-white rounded" ref={priceRef} onChange={handleOptions}>
              {priceOptions.map((i) => (
                <option key={i} value={i}>{i}</option>
              ))}
            </select>
            <div className='d-inline ms-2 h-100 w-20 fs-5 text-white'>
              â‚¹{finalPrice}/-
            </div>
          </div>

          <hr className="bg-secondary" />
          <button className="btn btn-success justify-center ms-2" onClick={handleAddtoCart}>
            Add to Cart
          </button>
        </div>
      </div>
    </div>
  );
}
