import React from 'react';
import { useCart, useDispatchCart } from '../components/ContextReducer';

export default function Cart() {
    const cartItems = useCart(); // Fetch items from cart context
    const dispatch = useDispatchCart();

    // Calculate the grand total of all cart items (price * quantity for each item)
    let totalPrice = cartItems.reduce((total, food) => total + (food.price * food.qty), 0);

    const handleRemove = (index) => {
        dispatch({ type: 'REMOVE', index });
    };

    if (cartItems.length === 0) {
        return (
            <div className="text-center m-5">
                Your Cart is Empty
            </div>
        );
    }

    return (
        <div className="container mt-3">
            <h2>Your Cart</h2>
            <table className="table table-bordered table-hover">
                <thead>
                    <tr>
                        <th scope="col">S.No</th>
                        <th scope="col">Food Name</th>
                        <th scope="col">Quantity</th>
                        <th scope="col">Option</th>
                        <th scope="col">Amount</th>
                        <th scope="col">Action</th>
                    </tr>
                </thead>
                <tbody>
                    {cartItems.map((item, index) => (
                        <tr key={index}>
                            <td>{index + 1}</td>
                            <td>{item.name}</td>
                            <td>{item.qty}</td>
                            <td>{item.size}</td>
                            <td>₹{item.price * item.qty}</td>
                            <td>
                                <button className="btn btn-danger" onClick={() => handleRemove(index)}>
                                    Remove
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <div className="fs-2">
                <h1>Grand Total: ₹{totalPrice}</h1> {/* Displaying the grand total */}
            </div>
            <div>
              <button className='btn g-success mt-5'>CheckOut</button>
            </div>
        </div>
    );
}
