import React from 'react';

export default function Card(props) {
  const { options, foodName, img } = props;
  const priceOptions = options ? Object.keys(options) : [];

  // Default image if no image is provided
  const imageUrl = img || "/path/to/default-image.jpeg";

  return (
    <div className="card mt-3" style={{ width: "18rem", maxHeight: "360px" }}>
      <img src={imageUrl} className="card-img-top" alt={foodName} />
      <div className="card-body">
        <h5 className="card-title">{foodName}</h5>
        <div className="container w-100">
          {/* Dropdown for price options */}
          <div className='mb-3'>
            <label htmlFor="priceOptions" className="form-label">Price Options</label>
            <select id="priceOptions" className='form-select bg-success text-white rounded'>
              {priceOptions.map((option) => (
                <option key={option} value={option}>{option}</option>
              ))}
            </select>
          </div>

          {/* Dropdown for quantity */}
          <div className='mb-3'>
            <label htmlFor="quantity" className="form-label">Quantity</label>
            <select id="quantity" className='form-select bg-success text-white rounded'>
              {Array.from(Array(6), (e, i) => (
                <option key={i + 1} value={i + 1}>{i + 1}</option>
              ))}
            </select>
          </div>

          {/* Dropdown for number of dishes (can be adjusted as needed) */}
          <div className='mb-3'>
            <label htmlFor="numberOfDishes" className="form-label">Number of Dishes</label>
            <select id="numberOfDishes" className='form-select bg-success text-white rounded'>
              {Array.from(Array(6), (e, i) => (
                <option key={i + 1} value={i + 1}>{i + 1}</option>
              ))}
            </select>
          </div>

          <div className='d-inline h-100 fs-5'>Total Price</div>
        </div>
      </div>
    </div>
  );
}
