const Footer = () => {
  const [userId, setUserId] = useState('');
  const [foodItemId, setFoodItemId] = useState('');
  const [rating, setRating] = useState('');
  const [review, setReview] = useState('');
  const [message, setMessage] = useState('');

  const handleCreate = async (e) => {
    e.preventDefault();
    setMessage('');

    try {
      const response = await fetch('http://localhost:5000/api/reviews', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ userId, foodItemId, rating, review }),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const data = await response.json();
      setMessage('Review created successfully!');
      console.log('Created Review:', data);
    } catch (error) {
      setMessage('An error occurred while creating the review. Please try again.');
      console.error('Error creating review:', error);
    }
  };

  return (
    <footer className='bg-dark text-light py-4'>
      <div className='container'>
        <h5>Submit a Review</h5>
        <form onSubmit={handleCreate}>
          <div className='mb-3'>
            <label htmlFor='userId' className='form-label'>User ID</label>
            <input
              type='text'
              className='form-control'
              id='userId'
              value={userId}
              onChange={(e) => setUserId(e.target.value)}
              required
            />
          </div>
          <div className='mb-3'>
            <label htmlFor='foodItemId' className='form-label'>Food Item ID</label>
            <input
              type='text'
              className='form-control'
              id='foodItemId'
              value={foodItemId}
              onChange={(e) => setFoodItemId(e.target.value)}
              required
            />
          </div>
          <div className='mb-3'>
            <label htmlFor='rating' className='form-label'>Rating (1-5)</label>
            <input
              type='number'
              className='form-control'
              id='rating'
              min='1'
              max='5'
              value={rating}
              onChange={(e) => setRating(e.target.value)}
              required
            />
          </div>
          <div className='mb-3'>
            <label htmlFor='review' className='form-label'>Review</label>
            <textarea
              className='form-control'
              id='review'
              rows='3'
              value={review}
              onChange={(e) => setReview(e.target.value)}
              required
            ></textarea>
          </div>
          <button type='submit' className='btn btn-primary'>Submit Review</button>
        </form>
        {message && <div className='mt-3 alert alert-info'>{message}</div>}
      </div>
    </footer>
  );
};

export default Footer;
