import React, { useState, useRef, useEffect } from 'react';
import { useDispatchCart, useCart } from '../components/ContextReducer';

export default function Card(props) {
    const dispatch = useDispatchCart();
    const [qty, setQty] = useState(1);
    const [size, setSize] = useState("");
    const priceRef = useRef();
    const options = props.options || {};
    const priceOptions = Object.keys(options);

    const { name, halfPrice, fullPrice, img, _id } = props.foodItem; // Destructure foodItem from props

    useEffect(() => {
        if (priceRef.current) {
            setSize(priceRef.current.value);
        }
    }, []);

    const handleQty = (e) => setQty(e.target.value);

    const handleOptions = (e) => setSize(e.target.value);

    const handleAddToCart = () => {
        if (size) {
            const finalPrice = qty * parseInt(options[size] || 0);
            dispatch({
                type: "ADD",
                id: _id,
                name: name,
                price: finalPrice,
                qty: qty,
                size: size,
            });
        } else {
            alert("Please select a size.");
        }
    };

    return (
        <div className="card mt-3 bg-dark text-light" style={{ width: "16rem", maxHeight: "360px" }}>
            <img
                src={`http://localhost:5000/${img}`}
                className="card-img-top"
                alt={name}
                style={{ height: "120px", objectFit: "fill" }}
            />
            <div className="card-body">
                <h5 className="card-title">{name}</h5>
                <p className="card-text">
                    Half Price: ₹{halfPrice} | Full Price: ₹{fullPrice}
                </p>
                <div className="container w-100 p-0" style={{ height: "38px" }}>
                    <select className="m-2 h-100 w-20 bg-success text-white rounded" onChange={handleQty} value={qty}>
                        {Array.from(Array(6), (e, i) => (
                            <option key={i + 1} value={i + 1}>
                                {i + 1}
                            </option>
                        ))}
                    </select>
                    <select
                        className="m-2 h-100 w-20 bg-success text-white rounded"
                        ref={priceRef}
                        onChange={handleOptions}
                    >
                        {priceOptions.map((option) => (
                            <option key={option} value={option}>
                                {option}
                            </option>
                        ))}
                    </select>
                    <div className="d-inline ms-2 h-100 w-20 fs-5 text-white">
                        ₹{qty * (parseInt(options[size] || 0))}/-
                    </div>
                </div>
                <hr className="bg-secondary" />
                <button className="btn btn-success justify-center ms-2" onClick={handleAddToCart}>
                    Add to Cart
                </button>
            </div>
        </div>
    );
}
