import React, { useEffect, useState } from 'react';
import Card from '../components/Card';
import Carousel from '../components/Carousel';
import Footer from '../components/Footer';
import Navbar from '../components/Navbar';

export default function Home() {
  const [foodItems, setFoodItems] = useState([]);
  const [search, setSearch] = useState('');

  const loadFoodItems = async () => {
    try {
      let response = await fetch("http://localhost:5000/api/getFoodItems", {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      let data = await response.json();
      if (Array.isArray(data)) {
        setFoodItems(data);
      }
    } catch (error) {
      console.error("Error loading food items:", error);
    }
  };

  useEffect(() => {
    loadFoodItems();
  }, []);

  return (
    <div className="bg-dark text-light min-vh-100">
      <Navbar />
      <Carousel onSearchChange={setSearch} />
      <div className="container py-4">
        {foodItems.length > 0 ? (
          <div className="row">
            {foodItems
              .filter(item => item.name.toLowerCase().includes(search.toLowerCase()))
              .map(filteredItem => (
                <div key={filteredItem.id} className="col-12 col-md-6 col-lg-3">
                  <Card foodItem={filteredItem} options={filteredItem.options[0]} />
                </div>
              ))}
          </div>
        ) : (
          <div className="text-center text-light">No Food Items Available</div>
        )}
      </div>
      <Footer />
    </div>
  );
}
