import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import Navbar from '../components/Navbar';

export default function Login() {
  const [credentials, setCredentials] = useState({ email: "", password: "" });
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch("http://localhost:5000/api/loginuser", {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email: credentials.email, password: credentials.password }),
      });

      const json = await response.json();

      if (json.success) {
        // Store the token and email in local storage
        localStorage.setItem("authToken", json.authToken);
        localStorage.setItem("userEmail", credentials.email);

        // If user is admin, navigate to an admin page
        const user = await fetchUserData(credentials.email); // Fetch user data to check if admin
        if (user.role === 'admin') {
          navigate("/admin/upload"); // Assuming an admin upload page exists
        } else {
          navigate("/"); // Otherwise, navigate to the home page
        }
      } else {
        alert("Invalid credentials. Please try again.");
      }
    } catch (error) {
      console.error('Error during login:', error);
      alert("Something went wrong. Please try again later.");
    }
  };

  const fetchUserData = async (email) => {
    const response = await fetch(`http://localhost:5000/api/getUserByEmail/${email}`);
    const data = await response.json();
    return data;
  };

  const onChange = (e) => {
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  };

  return (
    <div style={{ backgroundImage: 'url("https://images.pexels.com/photos/326278/pexels-photo-326278.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1")', height: '100vh', backgroundSize: 'cover' }}>
      <Navbar />
      <div className='container'>
        <form className='w-50 m-auto mt-5 border bg-dark border-success rounded' onSubmit={handleSubmit}>
          <div className="m-3">
            <label htmlFor="exampleInputEmail1" className="form-label">Email address</label>
            <input
              type="email"
              className="form-control"
              name='email'
              value={credentials.email}
              onChange={onChange}
              aria-describedby="emailHelp"
              required
            />
          </div>
          <div className="m-3">
            <label htmlFor="exampleInputPassword1" className="form-label">Password</label>
            <input
              type="password"
              className="form-control"
              name='password'
              value={credentials.password}
              onChange={onChange}
              required
            />
          </div>
          <button type="submit" className="m-3 btn btn-success">Submit</button>
          <Link to="/signup" className="m-3 mx-1 btn btn-danger">I'm a new user</Link>
        </form>
      </div>
    </div>
  );
}
