import React, { useState } from 'react';
import Navbar from '../components/Navbar';
import { useNavigate, Link } from 'react-router-dom';

export default function Login() {
  const [credentials, setCredentials] = useState({ email: "", password: "" });
  const [otp, setOtp] = useState("");
  const [otpRequested, setOtpRequested] = useState(false);
  let navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch("http://localhost:5000/api/loginuser", {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ email: credentials.email, password: credentials.password })
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const json = await response.json();

      if (!json.success) {
        alert("Enter valid Credentials");
      } else {
        localStorage.setItem("authToken", json.authToken);
        setOtpRequested(true);
      }
    } catch (error) {
      console.error("Error during fetch:", error);
      alert("An error occurred. Please try again.");
    }
  };

  const handleOtpSubmit = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/validateotp", {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ email: credentials.email, otp })
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const json = await response.json();

      if (json.success) {
        navigate("/");
      } else {
        alert("Invalid OTP or OTP expired");
      }
    } catch (error) {
      console.error("Error during fetch:", error);
      alert("An error occurred. Please try again.");
    }
  };

  const onChange = (e) => {
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  };

  return (
    <div style={{backgroundImage: 'url("https://images.pexels.com/photos/326278/pexels-photo-326278.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1")', height: '100vh', backgroundSize: 'cover' }}>
      <div>
        <Navbar />
      </div>
      <div className='container'>
        {!otpRequested ? (
          <form className='w-50 m-auto mt-5 border bg-dark border-success rounded' onSubmit={handleSubmit}>
            <div className="m-3">
              <label htmlFor="email" className="form-label">Email address</label>
              <input 
                type="email" 
                className="form-control" 
                name='email' 
                value={credentials.email} 
                onChange={onChange} 
                aria-describedby="emailHelp" 
              />
              <div id="emailHelp" className="form-text">We'll never share your email with anyone.</div>
            </div>
            <div className="m-3">
              <label htmlFor="password" className="form-label">Password</label>
              <input 
                type="password" 
                className="form-control" 
                value={credentials.password} 
                onChange={onChange} 
                name='password' 
              />
            </div>
            <button type="submit" className="m-3 btn btn-success">Submit</button>
            <Link to="/signup" className="m-3 mx-1 btn btn-danger">I am a user</Link>
          </form>
        ) : (
          <div className='w-50 m-auto mt-5 border bg-dark border-success rounded'>
            <div className="m-3">
              <label htmlFor="otp" className="form-label">Enter OTP</label>
              <input 
                type="text" 
                className="form-control" 
                value={otp} 
                onChange={(e) => setOtp(e.target.value)} 
                name='otp' 
              />
              <button type="button" className="m-3 btn btn-success" onClick={handleOtpSubmit}>Verify OTP</button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
