import React, { useEffect, useState, useRef } from 'react';
import { useDispatchCart } from '../components/ContextReducer';

export default function Card({ foodItem, options }) {
  const dispatch = useDispatchCart();
  const [qty, setQty] = useState(1);
  const [size, setSize] = useState("");
  const priceRef = useRef();

  useEffect(() => {
    if (priceRef.current) setSize(priceRef.current.value);
  }, []);

  const handleAddToCart = () => {
    if (size) {
      const finalPrice = qty * parseInt(options[size]);
      dispatch({
        type: "ADD",
        id: foodItem._id,
        name: foodItem.name,
        price: finalPrice,
        qty,
        size,
      });
    } else {
      alert("Please select a size.");
    }
  };

  return (
    <div className="card mt-3 bg-dark text-light" style={{ width: "16rem" }}>
      <img
        src={`http://localhost:5000/uploads/${foodItem.image}`}
        alt={foodItem.name}
        className="card-img-top"
        style={{ height: "120px", objectFit: "cover" }}
      />
      <div className="card-body">
        <h5 className="card-title">{foodItem.name}</h5>
        <div className="container">
          <select className="m-2" ref={priceRef} onChange={(e) => setSize(e.target.value)}>
            <option value="half">Half</option>
            <option value="full">Full</option>
          </select>
          <select className="m-2" onChange={(e) => setQty(e.target.value)} value={qty}>
            {Array.from({ length: 5 }, (_, i) => <option key={i} value={i + 1}>{i + 1}</option>)}
          </select>
          <div>₹{qty * (options[size] || 0)}/-</div>
        </div>
        <button className="btn btn-success mt-2" onClick={handleAddToCart}>Add to Cart</button>
      </div>
    </div>
  );
}
