import React, { useState } from 'react';

const UploadFoodItem = () => {
  const [foodItem, setFoodItem] = useState('');
  const [image, setImage] = useState(null);
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  // Retrieve token from localStorage
  const token = localStorage.getItem('token');

  // Handle form submission
  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!token) {
      setMessage('Authorization token not found. Please login.');
      return;
    }

    if (!foodItem || !image) {
      setMessage('Please fill in all fields');
      return;
    }

    // Create FormData to append the file and data
    const formData = new FormData();
    formData.append('foodItem', foodItem);
    formData.append('image', image);

    setLoading(true);

    try {
      const response = await fetch('http://yourapi.com/upload', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`, // Pass token in the Authorization header
        },
        body: formData,
      });

      const data = await response.json();

      if (response.ok) {
        setMessage('Food item uploaded successfully');
        // Optionally clear form fields after successful upload
        setFoodItem('');
        setImage(null);
      } else {
        setMessage(data.message || 'Failed to upload food item');
      }
    } catch (error) {
      console.error("Error uploading food item:", error);
      setMessage('Error uploading food item');
    }

    setLoading(false);
  };

  return (
    <div>
      <h2>Upload Food Item</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="foodItem">Food Item Name:</label>
          <input
            type="text"
            id="foodItem"
            value={foodItem}
            onChange={(e) => setFoodItem(e.target.value)}
            placeholder="Enter food item"
          />
        </div>

        <div>
          <label htmlFor="image">Food Item Image:</label>
          <input
            type="file"
            id="image"
            onChange={(e) => setImage(e.target.files[0])}
            accept="image/*"
          />
        </div>

        <button type="submit" disabled={loading}>
          {loading ? 'Uploading...' : 'Upload Food Item'}
        </button>
      </form>

      {message && <p>{message}</p>}
    </div>
  );
};

export default UploadFoodItem;
