import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function Footer() {
  const [reviews, setReviews] = useState([]);

  useEffect(() => {
    // Fetch reviews from the backend when the component mounts
    axios.get('/api/reviews')
      .then(response => setReviews(response.data))
      .catch(error => console.error('Error fetching reviews:', error));
  }, []);

  return (
    <footer className="bg-dark text-white mt-5 p-4 text-center">
      <div className="container">
        <div className="row">
          <div className="col-md-4">
            <h5>Contact Us</h5>
            <ul className="list-unstyled">
              <li>Address: 1234 Main St, Anytown, USA</li>
              <li>Email: contact@yourwebsite.com</li>
              <li>Phone: (123) 456-7890</li>
            </ul>
          </div>
          <div className="col-md-4">
            <h5>Follow Us</h5>
            <ul className="list-unstyled">
              <li><a href="#" className="text-white">Facebook</a></li>
              <li><a href="#" className="text-white">Twitter</a></li>
              <li><a href="#" className="text-white">Instagram</a></li>
            </ul>
          </div>
          <div className="col-md-4">
            <h5>User Reviews</h5>
            <ul className="list-unstyled">
              {reviews.length > 0 ? (
                reviews.map((review, index) => (
                  <li key={index}>"{review.comment}" - {review.user}</li>
                ))
              ) : (
                <li>No reviews yet. Be the first to leave one!</li>
              )}
            </ul>
          </div>
        </div>
        <div className="row">
          <div className="col-12">
            <p className="text-muted mt-3">© 2024 Your Company. All rights reserved.</p>
          </div>
        </div>
      </div>
    </footer>
  );
}
