import React, { useState } from 'react';
import Navbar from '../components/Navbar';
import { useNavigate, Link } from 'react-router-dom';

export default function Login() {
  const [credentials, setCredentials] = useState({ email: "", password: "" });
  const [loading, setLoading] = useState(false); // Loading state
  const [errorMessage, setErrorMessage] = useState(""); // Error message state
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Basic validation
    if (!credentials.email || !credentials.password) {
      setErrorMessage("Please fill in all fields.");
      return;
    }

    setLoading(true); // Set loading state to true
    setErrorMessage(""); // Clear previous error messages

    try {
      const response = await fetch("http://localhost:5000/api/loginuser", {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email: credentials.email, password: credentials.password }),
      });

      const json = await response.json();
      console.log(json);

      if (json.success) {
        // Store the token and email in local storage
        localStorage.setItem("authToken", json.authToken);
        localStorage.setItem("userEmail", credentials.email);

        // Navigate to the home page upon successful login
        navigate("/");
      } else {
        setErrorMessage("Invalid credentials. Please try again.");
      }
    } catch (error) {
      console.error('Error during login:', error);
      setErrorMessage("Something went wrong. Please try again later.");
    } finally {
      setLoading(false); // Set loading state to false
    }
  };

  const onChange = (e) => {
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  };

  return (
    <div style={{ backgroundImage: 'url("https://images.pexels.com/photos/326278/pexels-photo-326278.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1")', height: '100vh', backgroundSize: 'cover' }}>
      <Navbar />
      <div className='container'>
        <form className='w-50 m-auto mt-5 border bg-dark border-success rounded' onSubmit={handleSubmit}>
          <div className="m-3">
            <label htmlFor="exampleInputEmail1" className="form-label">Email address</label>
            <input
              type="email"
              className="form-control"
              name='email'
              value={credentials.email}
              onChange={onChange}
              aria-describedby="emailHelp"
              required
            />
            <div id="emailHelp" className="form-text">We'll never share your email with anyone.</div>
          </div>
          <div className="m-3">
            <label htmlFor="exampleInputPassword1" className="form-label">Password</label>
            <input
              type="password"
              className="form-control"
              name='password'
              value={credentials.password}
              onChange={onChange}
              required
            />
          </div>

          {errorMessage && <div className="alert alert-danger" role="alert">{errorMessage}</div>} {/* Error message display */}

          <button type="submit" className="m-3 btn btn-success" disabled={loading}>
            {loading ? "Logging in..." : "Submit"}
          </button> {/* Show loading state */}

          <Link to="/signup" className="m-3 mx-1 btn btn-danger">I'm a new user</Link>
        </form>
      </div>
    </div>
  );
}
