import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from "react-router-dom";
import Badge from "@mui/material/Badge";
import ShoppingCartIcon from "@mui/icons-material/ShoppingCart";
import { useCart } from '../components/ContextReducer';
import Modal from '../Modal';
import Cart from '../screens/Cart';

export default function Navbar(props) {
    const [cartView, setCartView] = useState(false);
    const [isAuthenticated, setIsAuthenticated] = useState(!!localStorage.getItem("authToken"));
    const [profilePic, setProfilePic] = useState(null); // State to hold the profile picture
    const navigate = useNavigate();

    useEffect(() => {
        const handleAuthChange = () => {
            setIsAuthenticated(!!localStorage.getItem("authToken"));
        };

        window.addEventListener('storage', handleAuthChange);
        return () => {
            window.removeEventListener('storage', handleAuthChange);
        };
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('authToken');
        setIsAuthenticated(false);
        navigate("/signup");
    };

    const handleImageUpload = async (e) => {
        const file = e.target.files[0];
        if (file) {
            const formData = new FormData();
            formData.append("profilePicture", file);

            try {
                const response = await fetch("http://localhost:5000/api/uploadProfilePic", {
                    method: "POST",
                    body: formData,
                });
                const data = await response.json();
                if (data.success) {
                    setProfilePic(data.imageUrl); // Update state with the uploaded image URL
                } else {
                    alert("Failed to upload profile picture");
                }
            } catch (error) {
                console.error("Error uploading profile picture:", error);
            }
        }
    };

    const items = useCart();

    return (
        <div>
            <nav className="navbar navbar-expand-lg navbar-dark bg-success position-sticky"
                style={{ boxShadow: "0px 10px 20px black", position: "fixed", zIndex: "10", width: "100%" }}>
                <div className="container-fluid">
                    <Link className="navbar-brand fs-1 fst-italic" to="/">GoFood</Link>
                    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className="collapse navbar-collapse" id="navbarSupportedContent">
                        <ul className="navbar-nav me-auto mb-2 mb-lg-0">
                            <li className="nav-item">
                                <Link className="nav-link fs-5 mx-3 active" aria-current="page" to="/">Home</Link>
                            </li>
                            {isAuthenticated && (
                                <li className="nav-item">
                                    <Link className="nav-link fs-5 mx-3 active" aria-current="page" to="/MyOrder">My Orders</Link>
                                </li>
                            )}
                        </ul>
                        <div className="d-flex align-items-center">
                            {isAuthenticated && profilePic ? (
                                <div className="profile-container">
                                    <img
                                        src={profilePic}
                                        alt="Profile"
                                        className="rounded-circle"
                                        style={{ width: "40px", height: "40px", marginRight: "10px" }}
                                    />
                                    <label htmlFor="file-upload" className="btn btn-secondary btn-sm rounded-circle">
                                        <i className="fas fa-camera"></i>
                                    </label>
                                    <input
                                        id="file-upload"
                                        type="file"
                                        style={{ display: "none" }}
                                        onChange={handleImageUpload}
                                    />
                                </div>
                            ) : (
                                <label htmlFor="file-upload" className="btn btn-white text-success">
                                    Upload Profile Picture
                                </label>
                            )}
                            {!isAuthenticated ? (
                                <>
                                    <Link className="btn bg-white text-success mx-1" to="/login">Login</Link>
                                    <Link className="btn bg-white text-success mx-1" to="/signup">Signup</Link>
                                </>
                            ) : (
                                <>
                                    <div className="btn bg-white text-success mx-2" onClick={() => setCartView(true)}>
                                        <Badge color="secondary" badgeContent={items.length}>
                                            <ShoppingCartIcon />
                                        </Badge>
                                        Cart
                                    </div>
                                    <button onClick={handleLogout} className="btn bg-white text-success mx-2">Logout</button>
                                </>
                            )}
                        </div>
                    </div>
                </div>
            </nav>
            {cartView && (
                <Modal onClose={() => setCartView(false)}>
                    <Cart />
                </Modal>
            )}
        </div>
    );
}
