import React, { useState, useEffect } from 'react';

export default function Footer() {
    const [user, setUser] = useState('');
    const [comment, setComment] = useState('');
    const [rating, setRating] = useState(1);
    const [reviews, setReviews] = useState([]);
    const [error, setError] = useState(null);

    // Fetch reviews from the backend
    const fetchReviews = async () => {
        try {
            const response = await fetch('http://localhost:5000/api/reviews');
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const data = await response.json();
            setReviews(data);
        } catch (err) {
            console.error("Error fetching reviews:", err);
            setError("Failed to load reviews.");
        }
    };

    useEffect(() => {
        fetchReviews();
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch('http://localhost:5000/api/reviews', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ user, comment, rating }),
            });

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            setUser('');
            setComment('');
            setRating(1);
            fetchReviews(); // Refresh reviews after submission
        } catch (err) {
            console.error("Error submitting review:", err);
            setError("Failed to submit review.");
        }
    };

    return (
        <footer className="bg-dark text-white py-4">
            <div className="container">
                <h5 className="text-center mb-4">Submit Your Review</h5>
                <form onSubmit={handleSubmit} className="mb-4">
                    <div className="mb-3">
                        <label htmlFor="user" className="form-label">Name</label>
                        <input
                            type="text"
                            className="form-control"
                            id="user"
                            value={user}
                            onChange={(e) => setUser(e.target.value)}
                            required
                        />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="comment" className="form-label">Comment</label>
                        <textarea
                            className="form-control"
                            id="comment"
                            rows="3"
                            value={comment}
                            onChange={(e) => setComment(e.target.value)}
                            required
                        ></textarea>
                    </div>
                    <div className="mb-3">
                        <label htmlFor="rating" className="form-label">Rating (1-5)</label>
                        <input
                            type="number"
                            className="form-control"
                            id="rating"
                            min="1"
                            max="5"
                            value={rating}
                            onChange={(e) => setRating(e.target.value)}
                            required
                        />
                    </div>
                    {error && <div className="alert alert-danger">{error}</div>}
                    <button type="submit" className="btn btn-success w-100">Submit Review</button>
                </form>
                <h5 className="text-center mb-4">Reviews</h5>
                {reviews.length > 0 ? (
                    <ul className="list-group">
                        {reviews.map((review) => (
                            <li key={review._id} className="list-group-item bg-secondary text-white">
                                <h6>{review.user} - {review.rating}/5</h6>
                                <p>{review.comment}</p>
                                <small>{new Date(review.date).toLocaleDateString()}</small>
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p className="text-center">No reviews yet.</p>
                )}
            </div>
        </footer>
    );
}
