import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import Navbar from '../components/Navbar';

export default function Login() {
  const [credentials, setCredentials] = useState({ email: "", password: "" });
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch("http://localhost:5000/api/loginuser", {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email: credentials.email, password: credentials.password }),
      });


      const json = await response.json();
      const salt = await bcrypt.genSalt(10);
    user.password = await bcrypt.hash(password, salt);
      console.log(credentials.password);

      if (json.success) {
        // Store the token and email in local storage
        localStorage.setItem("authToken", json.authToken);
        localStorage.setItem("userEmail", credentials.email);

        // Fetch the user data to check if they are an admin
        const user = await fetchUserData(credentials.email);

        // Check if the role is admin or user and redirect accordingly
        if (user && user.role === 'admin') {
          navigate("/"); 
          // Redirect to admin page
        } else {
          navigate("/"); // Redirect to home page for regular user
        }
      } else {
        alert("Invalid credentials. Please try again.");
      }
    } catch (error) {
      console.error('Error during login:', error);
      alert("Something went wrong. Please try again later.");
    }
  };

  const fetchUserData = async (email) => {
    try {
      const response = await fetch(`http://localhost:5000/api/getUserByEmail/${email}`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('authToken')}`,
        },
      });
      const data = await response.json();
      return data;
    } catch (error) {
      console.error('Error fetching user data:', error);
      return null;
    }
  };

  const onChange = (e) => {
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  };

  return (
    <div style={{ backgroundImage: 'url("https://images.pexels.com/photos/326278/pexels-photo-326278.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1")', height: '100vh', backgroundSize: 'cover' }}>
      <Navbar />
      <div className='container'>
        <form className='w-50 m-auto mt-5 border bg-dark border-success rounded' onSubmit={handleSubmit}>
          <div className="m-3">
            <label htmlFor="exampleInputEmail1" className="form-label">Email address</label>
            <input
              type="email"
              className="form-control"
              name='email'
              value={credentials.email}
              onChange={onChange}
              aria-describedby="emailHelp"
              required
            />
          </div>
          <div className="m-3">
            <label htmlFor="exampleInputPassword1" className="form-label">Password</label>
            <input
              type="password"
              className="form-control"
              name='password'
              value={credentials.password}
              onChange={onChange}
              required
            />
          </div>
          <button type="submit" className="m-3 btn btn-success">Submit</button>
          <Link to="/signup" className="m-3 mx-1 btn btn-danger">I'm a new user</Link>
        </form>
      </div>
    </div>
  );
}
