import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Footer = () => {
  const [reviews, setReviews] = useState([]);
  const [newReview, setNewReview] = useState({ userId: '', foodItemId: '', rating: '', review: '' });
  const [editReview, setEditReview] = useState(null);

  useEffect(() => {
    fetchReviews();
  }, []);

  const fetchReviews = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/reviews');
      setReviews(response.data);
    } catch (error) {
      console.error('Error fetching reviews:', error);
    }
  };

  const handleAddReview = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/reviews', newReview);
      setReviews([...reviews, response.data]);
      setNewReview({ userId: '', foodItemId: '', rating: '', review: '' });
    } catch (error) {
      console.error('Error adding review:', error);
    }
  };

  const handleUpdateReview = async (id) => {
    try {
      const response = await axios.put(`http://localhost:5000/api/reviews/${id}`, editReview);
      setReviews(reviews.map(review => review._id === id ? response.data : review));
      setEditReview(null);
    } catch (error) {
      console.error('Error updating review:', error);
    }
  };

  const handleDeleteReview = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/reviews/${id}`);
      setReviews(reviews.filter(review => review._id !== id));
    } catch (error) {
      console.error('Error deleting review:', error);
    }
  };

  return (
    <footer>
      <h2>User Reviews</h2>

      <div>
        <h3>Add Review</h3>
        <input
          type="text"
          placeholder="User ID"
          value={newReview.userId}
          onChange={(e) => setNewReview({ ...newReview, userId: e.target.value })}
        />
        <input
          type="text"
          placeholder="Food Item ID"
          value={newReview.foodItemId}
          onChange={(e) => setNewReview({ ...newReview, foodItemId: e.target.value })}
        />
        <input
          type="number"
          placeholder="Rating"
          value={newReview.rating}
          onChange={(e) => setNewReview({ ...newReview, rating: e.target.value })}
        />
        <textarea
          placeholder="Review"
          value={newReview.review}
          onChange={(e) => setNewReview({ ...newReview, review: e.target.value })}
        />
        <button onClick={handleAddReview}>Add Review</button>
      </div>

      <div>
        <h3>Reviews List</h3>
        {reviews.map((review) => (
          <div key={review._id}>
            <p><strong>User ID:</strong> {review.userId}</p>
            <p><strong>Food Item ID:</strong> {review.foodItemId}</p>
            <p><strong>Rating:</strong> {review.rating}</p>
            <p><strong>Review:</strong> {review.review}</p>
            <button onClick={() => setEditReview(review)}>Edit</button>
            <button onClick={() => handleDeleteReview(review._id)}>Delete</button>
          </div>
        ))}
      </div>

      {editReview && (
        <div>
          <h3>Edit Review</h3>
          <input
            type="text"
            placeholder="User ID"
            value={editReview.userId}
            onChange={(e) => setEditReview({ ...editReview, userId: e.target.value })}
          />
          <input
            type="text"
            placeholder="Food Item ID"
            value={editReview.foodItemId}
            onChange={(e) => setEditReview({ ...editReview, foodItemId: e.target.value })}
          />
          <input
            type="number"
            placeholder="Rating"
            value={editReview.rating}
            onChange={(e) => setEditReview({ ...editReview, rating: e.target.value })}
          />
          <textarea
            placeholder="Review"
            value={editReview.review}
            onChange={(e) => setEditReview({ ...editReview, review: e.target.value })}
          />
          <button onClick={() => handleUpdateReview(editReview._id)}>Update Review</button>
        </div>
      )}
    </footer>
  );
};

export default Footer;
