import React, { useState } from 'react';

export default function Footer() {
  const [formData, setFormData] = useState({
    userId: '',
    foodItemId: '',
    rating: '',
    review: '',
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState({ type: '', text: '' });

  const apiUrl = 'http://localhost:5000/api/reviews';

  // Handle input change
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Validate form data
  const validateForm = () => {
    const { userId, foodItemId, rating, review } = formData;
    if (!userId || !foodItemId || !rating || !review) {
      setMessage({ type: 'warning', text: 'Please fill in all fields.' });
      return false;
    }
    if (rating < 1 || rating > 5) {
      setMessage({ type: 'warning', text: 'Rating must be between 1 and 5.' });
      return false;
    }
    return true;
  };

  // Reset form
  const resetForm = () => {
    setFormData({
      userId: '',
      foodItemId: '',
      rating: '',
      review: '',
    });
  };

  // Create a new review
  const handleCreateReview = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;
    setLoading(true);
    try {
      const response = await fetch(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });
      if (!response.ok) {
        throw new Error('Failed to create review.');
      }
      resetForm();
      setMessage({ type: 'success', text: 'Review added successfully.' });
    } catch (error) {
      setMessage({ type: 'danger', text: error.message });
    } finally {
      setLoading(false);
    }
  };

  return (
    <footer className="bg-dark text-light py-5">
      <div className="container">
        <h2 className="mb-4">Submit Your Review</h2>

        {/* Alert Message */}
        {message.text && (
          <div className={`alert alert-${message.type}`} role="alert">
            {message.text}
          </div>
        )}

        {/* Review Form */}
        <form onSubmit={handleCreateReview} className="mb-5">
          <div className="row g-3">
            <div className="col-md-3">
              <label htmlFor="userId" className="form-label">
                User ID
              </label>
              <input
                type="text"
                className="form-control"
                id="userId"
                name="userId"
                value={formData.userId}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className="col-md-3">
              <label htmlFor="foodItemId" className="form-label">
                Food Item ID
              </label>
              <input
                type="text"
                className="form-control"
                id="foodItemId"
                name="foodItemId"
                value={formData.foodItemId}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className="col-md-2">
              <label htmlFor="rating" className="form-label">
                Rating
              </label>
              <input
                type="number"
                className="form-control"
                id="rating"
                name="rating"
                min="1"
                max="5"
                value={formData.rating}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className="col-md-4">
              <label htmlFor="review" className="form-label">
                Review
              </label>
              <textarea
                className="form-control"
                id="review"
                name="review"
                rows="1"
                value={formData.review}
                onChange={handleInputChange}
                required
              ></textarea>
            </div>
          </div>
          <div className="mt-3">
            <button
              type="submit"
              className="btn btn-success"
              disabled={loading}
            >
              {loading ? 'Processing...' : 'Submit Review'}
            </button>
          </div>
        </form>

        {/* Footer Note */}
        <div className="text-center mt-5">
          <span className="text-muted">© 2024 <i>GoFood</i>, Inc</span>
        </div>
      </div>
    </footer>
  );
}
