import React, { useEffect, useState } from 'react';

export default function Footer() {
  const [reviews, setReviews] = useState([]);
  const [userId, setUserId] = useState('');
  const [foodItemId, setFoodItemId] = useState('');
  const [rating, setRating] = useState('');
  const [review, setReview] = useState('');
  const [editMode, setEditMode] = useState(false);
  const [currentReviewId, setCurrentReviewId] = useState(null);

  // Fetch all reviews
  const fetchReviews = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/reviews');
      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }
      const data = await response.json();
      setReviews(data);
    } catch (error) {
      console.error('Error fetching reviews:', error);
    }
  };

  useEffect(() => {
    fetchReviews();
  }, []);

  // Create a new review
  const handleCreateReview = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/reviews', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ userId, foodItemId, rating, review }),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const newReview = await response.json();
      setReviews([...reviews, newReview]);
      setUserId('');
      setFoodItemId('');
      setRating('');
      setReview('');
    } catch (error) {
      console.error('Error creating review:', error);
    }
  };

  // Update an existing review
  const handleUpdateReview = async () => {
    try {
      const response = await fetch(`http://localhost:5000/api/reviews/${currentReviewId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ userId, foodItemId, rating, review }),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const updatedReview = await response.json();
      setReviews(
        reviews.map((r) => (r._id === currentReviewId ? updatedReview : r))
      );
      setEditMode(false);
      setUserId('');
      setFoodItemId('');
      setRating('');
      setReview('');
      setCurrentReviewId(null);
    } catch (error) {
      console.error('Error updating review:', error);
    }
  };

  // Delete a review
  const handleDeleteReview = async (id) => {
    try {
      const response = await fetch(`http://localhost:5000/api/reviews/${id}`, {
        method: 'DELETE',
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      setReviews(reviews.filter((review) => review._id !== id));
    } catch (error) {
      console.error('Error deleting review:', error);
    }
  };

  // Set review data for editing
  const handleEditReview = (review) => {
    setUserId(review.userId);
    setFoodItemId(review.foodItemId);
    setRating(review.rating);
    setReview(review.review);
    setCurrentReviewId(review._id);
    setEditMode(true);
  };

  return (
    <div className="container">
      <h2>Reviews</h2>
      <div>
        <input
          type="text"
          placeholder="User ID"
          value={userId}
          onChange={(e) => setUserId(e.target.value)}
        />
        <input
          type="text"
          placeholder="Food Item ID"
          value={foodItemId}
          onChange={(e) => setFoodItemId(e.target.value)}
        />
        <input
          type="number"
          placeholder="Rating"
          value={rating}
          onChange={(e) => setRating(e.target.value)}
        />
        <textarea
          placeholder="Review"
          value={review}
          onChange={(e) => setReview(e.target.value)}
        ></textarea>
        {editMode ? (
          <button onClick={handleUpdateReview}>Update Review</button>
        ) : (
          <button onClick={handleCreateReview}>Submit Review</button>
        )}
      </div>
      <ul>
        {reviews.map((review) => (
          <li key={review._id}>
            <strong>{review.userId}</strong>: {review.review} (Rating: {review.rating})
            <button onClick={() => handleEditReview(review)}>Edit</button>
            <button onClick={() => handleDeleteReview(review._id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
