import React from 'react';

export default function Card(props) {
  const { options, foodName, img } = props;
  const sizeOptions = ['Half', 'Full']; // Options for size
  const quantityOptions = Array.from(Array(6), (e, i) => i + 1); // Quantity options from 1 to 6

  // Default image if none is provided
  const imageUrl = img || "/path/to/default-image.jpeg";

  return (
    <div className="card mt-3" style={{ width: "18rem", maxHeight: "360px" }}>
      <img src={imageUrl} className="card-img-top" alt={foodName} />
      <div className="card-body">
        <h5 className="card-title">{foodName}</h5>
        <div className="container w-100">
          {/* Dropdown for size options (e.g., Half, Full) */}
          <div className='mb-3'>
            <label htmlFor="sizeOptions" className="form-label">Size</label>
            <select id="sizeOptions" className='form-select bg-success text-white rounded'>
              {sizeOptions.map((size) => (
                <option key={size} value={size}>{size}</option>
              ))}
            </select>
          </div>

          {/* Dropdown for quantity options (e.g., number of items) */}
          <div className='mb-3'>
            <label htmlFor="quantity" className="form-label">Quantity</label>
            <select id="quantity" className='form-select bg-success text-white rounded'>
              {quantityOptions.map((quantity) => (
                <option key={quantity} value={quantity}>{quantity}</option>
              ))}
            </select>
          </div>

          <div className='d-inline h-100 fs-5'>Total Price</div>
        </div>
      </div>
    </div>
  );
}
