import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';  // Regular Bootstrap CSS
import 'bootstrap-dark-5/dist/css/bootstrap-dark.min.css'; // Dark Bootstrap theme (if used)
import 'bootstrap/dist/js/bootstrap.bundle.min'; // Bootstrap JavaScript


import Home from './screens/Home';
import Login from './screens/Login';
import Signup from './screens/Signup';
import MyOrder from './screens/MyOrder';
import { CartProvider } from './components/ContextReducer';
import {
  BrowserRouter as Router,
  Routes,
  Route
} from "react-router-dom";

function App() {
  return (
    <CartProvider>
      <Router>
        <div>
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/login" element={<Login />} />
            <Route path="/signup" element={<Signup />} />
            <Route path="/myorder" element={<MyOrder />} />
          </Routes>
        </div>
      </Router>
    </CartProvider>
  );
}

export default App;
