import React, { useEffect, useState } from 'react';
import Card from '../components/Card';
import Carousel from '../components/Carousel';
import Footer from '../components/Footer';
import Navbar from '../components/Navbar';

export default function Home() {
  const [foodCat, setFoodCat] = useState([]); // Food Categories
  const [foodItems, setFoodItems] = useState([]); // Food Items
  const [search, setSearch] = useState(''); // Search term
  const [loading, setLoading] = useState(true); // Loading state
  const [errorMessage, setErrorMessage] = useState(''); // Error message state

  // Fetch food items and categories from the API
  const loadFoodItems = async () => {
    setLoading(true);
    setErrorMessage('');
    try {
      const response = await fetch('http://localhost:5000/api/foodItems', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const data = await response.json();

      if (Array.isArray(data) && data.length === 2) {
        setFoodItems(data[0] || []);
        setFoodCat(data[1] || []);
      } else {
        throw new Error('Unexpected response format');
      }
    } catch (error) {
      console.error('Error loading food items:', error);
      setErrorMessage('Failed to load food data. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  // Effect hook to load food items and categories on component mount
  useEffect(() => {
    loadFoodItems();
  }, []);

  // Filtered food items based on category and search query
  const filteredFoodItems = foodItems.filter(
    (item) =>
      item.name.toLowerCase().includes(search.toLowerCase()) &&
      (foodCat.length === 0 || foodCat.includes(item.CategoryName))
  );

  return (
    <div className="bg-dark text-light min-vh-100">
      <Navbar />
      <Carousel onSearchChange={setSearch} />
      <div className="container py-4">
        {loading ? (
          <div className="text-center text-light">Loading...</div>
        ) : errorMessage ? (
          <div className="text-center text-danger">{errorMessage}</div>
        ) : foodCat.length > 0 ? (
          foodCat.map((category) => (
            <div className="row mb-3" key={category}>
              <div className="fs-3 m-3">{category}</div>
              <hr
                id="hr-success"
                style={{
                  height: '4px',
                  backgroundImage: 'linear-gradient(left, rgb(0, 255, 137), rgb(0, 0, 0))',
                }}
              />
              {filteredFoodItems.length > 0 ? (
                filteredFoodItems
                  .filter((item) => item.CategoryName === category)
                  .map((filteredItem) => (
                    <div key={filteredItem._id} className="col-12 col-md-6 col-lg-3">
                      <Card
                        foodItem={filteredItem}
                        imageUrl={`http://localhost:5000/uploads/${filteredItem.image}`}
                        options={filteredItem.options ? filteredItem.options[0] : {}}
                      />
                    </div>
                  ))
              ) : (
                <div className="text-center text-light">No items available in this category.</div>
              )}
            </div>
          ))
        ) : (
          <div className="text-center text-light">No Categories Available</div>
        )}
      </div>
      <Footer />
    </div>
  );
}
