import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Footer = () => {
  const [userId, setUserId] = useState('');
  const [foodItemId, setFoodItemId] = useState('');
  const [rating, setRating] = useState('');
  const [review, setReview] = useState('');
  const [reviews, setReviews] = useState([]);
  const [editId, setEditId] = useState(null);

  useEffect(() => {
    // Fetch all reviews on component mount
    const fetchReviews = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/reviews');
        setReviews(response.data);
      } catch (error) {
        console.error('Error fetching reviews:', error);
      }
    };
    fetchReviews();
  }, []);

  const handleAddReview = async (e) => {
    e.preventDefault();
    try {
      const newReview = { userId, foodItemId, rating, review };
      await axios.post('http://localhost:5000/api/reviews', newReview);
      setUserId('');
      setFoodItemId('');
      setRating('');
      setReview('');
      // Refresh reviews
      const response = await axios.get('http://localhost:5000/api/reviews');
      setReviews(response.data);
    } catch (error) {
      console.error('Error adding review:', error);
    }
  };

  const handleEditReview = async (e) => {
    e.preventDefault();
    try {
      const updatedReview = { userId, foodItemId, rating, review };
      await axios.put(`http://localhost:5000/api/reviews/${editId}`, updatedReview);
      setUserId('');
      setFoodItemId('');
      setRating('');
      setReview('');
      setEditId(null);
      // Refresh reviews
      const response = await axios.get('http://localhost:5000/api/reviews');
      setReviews(response.data);
    } catch (error) {
      console.error('Error updating review:', error);
    }
  };

  const handleDeleteReview = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/reviews/${id}`);
      // Refresh reviews
      const response = await axios.get('http://localhost:5000/api/reviews');
      setReviews(response.data);
    } catch (error) {
      console.error('Error deleting review:', error);
    }
  };

  return (
    <footer>
      <h2>Review Management</h2>
      <form onSubmit={editId ? handleEditReview : handleAddReview}>
        <div>
          <label>User ID:</label>
          <input
            type="text"
            value={userId}
            onChange={(e) => setUserId(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Food Item ID:</label>
          <input
            type="text"
            value={foodItemId}
            onChange={(e) => setFoodItemId(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Rating:</label>
          <input
            type="number"
            value={rating}
            onChange={(e) => setRating(e.target.value)}
            min="1"
            max="5"
            required
          />
        </div>
        <div>
          <label>Review:</label>
          <textarea
            value={review}
            onChange={(e) => setReview(e.target.value)}
            required
          ></textarea>
        </div>
        <button type="submit">{editId ? 'Update Review' : 'Add Review'}</button>
      </form>
      <h3>Existing Reviews</h3>
      <ul>
        {reviews.map((review) => (
          <li key={review._id}>
            <p><strong>User ID:</strong> {review.userId}</p>
            <p><strong>Food Item ID:</strong> {review.foodItemId}</p>
            <p><strong>Rating:</strong> {review.rating}</p>
            <p><strong>Review:</strong> {review.review}</p>
            <button onClick={() => {
              setEditId(review._id);
              setUserId(review.userId);
              setFoodItemId(review.foodItemId);
              setRating(review.rating);
              setReview(review.review);
            }}>Edit</button>
            <button onClick={() => handleDeleteReview(review._id)}>Delete</button>
          </li>
        ))}
      </ul>
    </footer>
  );
};

export default Footer;
