import React, { useState } from 'react';
import { useDispatchCart, useCart } from '../components/ContextReducer';

export default function Card(props) {
    const dispatch = useDispatchCart();
    const [qty, setQty] = useState(1);  // Default quantity to 1
    const [size, setSize] = useState(""); // Default size as empty
    const options = props.options || {}; // Get the options from props
    const priceOptions = Object.keys(options); // Get the size options

    // Handle change in quantity
    const handleQty = (e) => {
        setQty(e.target.value);
    };

    // Handle change in size selection
    const handleOptions = (e) => {
        setSize(e.target.value);
    };

    // Handle adding to cart
    const handleAddtoCart = () => {
        if (size) { // Ensure size is selected before adding to cart
            const finalPrice = qty * parseInt(options[size] || 0);
            dispatch({
                type: "ADD",
                id: props.foodItem._id,
                name: props.foodItem.name,
                price: finalPrice,
                qty: qty,
                size: size
            });
        } else {
            alert("Please select a size.");
        }
    };

    // Calculate full price and half price
    const fullPrice = qty * (parseInt(options[size] || 0));
    const halfPrice = fullPrice / 2;

    return (
        <div className="card mt-3 bg-dark text-light" style={{ width: "16rem", maxHeight: "360px" }}>
            <img 
                src={props.foodItem.img} 
                className="card-img-top" 
                alt={props.foodItem.name} 
                style={{ height: "120px", objectFit: "cover" }} 
            />
            <div className="card-body">
                <h5 className="card-title">{props.foodItem.name}</h5>

                <div className='container w-100 p-0' style={{ height: "38px" }}>
                    {/* Quantity Selector */}
                    <select 
                        className="m-2 h-100 w-20 bg-success text-white rounded" 
                        onChange={handleQty} 
                        value={qty}
                    >
                        {Array.from(Array(6), (e, i) => (
                            <option key={i + 1} value={i + 1}>{i + 1}</option>
                        ))}
                    </select>

                    {/* Size Selector */}
                    <select 
                        className="m-2 h-100 w-20 bg-success text-white rounded" 
                        onChange={handleOptions} 
                        value={size}
                    >
                        <option value="">Select Size</option>
                        {priceOptions.map((option) => (
                            <option key={option} value={option}>{option}</option>
                        ))}
                    </select>

                    {/* Price Display */}
                    <div className='d-inline ms-2 h-100 w-20 fs-5 text-white'>
                        ₹{fullPrice}/-
                    </div>
                </div>

                {/* Full Price and Half Price Display */}
                {size && (
                    <div className="d-flex justify-content-between mt-2 text-white">
                        <div>Full Price: ₹{fullPrice}/-</div>
                        <div>Half Price: ₹{halfPrice.toFixed(2)}/-</div>
                    </div>
                )}

                <hr className="bg-secondary" />

                {/* Add to Cart Button */}
                <button className='btn btn-success justify-center ms-2' onClick={handleAddtoCart}>Add to Cart</button>
            </div>
        </div>
    );
}
