import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useDispatchCart } from './ContextReducer';

export default function Card(props) {
  let dispatch=useDispatchCart();
  let navigate = useNavigate();
  const [qty, setQty] = useState(1);
  const [size, setSize] = useState("");
  const priceRef = useRef();
  let options = props.options;
  let priceOptions = Object.keys(options);
  let data=useCart(_)

  const handleQty = (e) => {
    setQty(e.target.value);
  };

  const handleOptions = (e) => {
    setSize(e.target.value);
  };

  useEffect(() => {
    setSize(priceRef.current.value);
  }, []);

  const handleAddtoCart = async() => {
    await dispatch({type:"ADD",id:props.foodItem._id,name:props.foodItem.name,price:props.finalPrice,qty:qty,size:size})
    console.log(dispatch)
  };


  let finalPrice = qty * (options[size] || 0);

  return (
    <div>
      <div className="card mt-3 bg-dark text-light" style={{ width: "16rem", maxHeight: "360px" }}>
        <img src={props.foodItem.img} className="card-img-top" alt="..." style={{ height: "120px", objectFit: "fill" }} />
        <div className="card-body">
          <h5 className="card-title">{props.foodItem.name}</h5>
          <div className='container w-100 p-0' style={{ height: "38px" }}>
            <select className="m-2 h-100 w-20 bg-success text-white rounded" onChange={handleQty}>
              {Array.from(Array(6), (e, i) => {
                return (
                  <option key={i + 1} value={i + 1}>{i + 1}</option>)
              })}
            </select>
            <select className="m-2 h-100 w-20 bg-success text-white rounded" ref={priceRef} onChange={handleOptions}>
              {priceOptions.map((i) => {
                return <option key={i} value={i}>{i}</option>
              })}
            </select>
            <div className='d-inline ms-2 h-100 w-20 fs-5 text-white'>
              â‚¹{finalPrice}/-
            </div>
          </div>

          <hr className="bg-secondary" />
          <button className={'btn btn-success justify-center ms-2'} onClick={handleAddtoCart}>Add to Cart</button>
        </div>
      </div>
    </div>
  );
}
