import React, { useEffect, useState } from 'react';
import Card from '../components/Card';
import Carousel from '../components/Carousel';
import Footer from '../components/Footer';
import Navbar from '../components/Navbar';

export default function Home() {
  const [foodCat, setFoodCat] = useState([]); // Ensure it's an empty array
  const [foodItems, setFoodItems] = useState([]); // Ensure it's an empty array
  const [search, setSearch] = useState('');
  const [loading, setLoading] = useState(true); // Add loading state

  const loadFoodItems = async () => {
    setLoading(true); // Set loading to true when fetching starts
    try {
      let response = await fetch("http://localhost:5000/api/foodData", {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      let data = await response.json();
      console.log("API Response:", data);

      if (Array.isArray(data) && data.length === 2) {
        setFoodItems(data[0] || []);
        setFoodCat(data[1] || []);
      } else {
        console.error("Unexpected response format:", data);
      }
    } catch (error) {
      console.error("Error loading food items:", error);
    } finally {
      setLoading(false); // Set loading to false after fetching completes
    }
  };

  useEffect(() => {
    loadFoodItems();
  }, []);

  return (
    <div>
      <Navbar />
      <Carousel />
      <div className='container'>
        {loading ? (
          <div>Loading...</div> // Display loading state
        ) : foodCat.length > 0 ? (
          foodCat.map((data) => {
            return (
              <div className='row mb-3' key={data.id}>
                <div className='fs-3 m-3'>
                  {data.CategoryName}
                </div>
                <hr id="hr-success" style={{ height: "4px", backgroundImage: "-webkit-linear-gradient(left,rgb(0, 255, 137),rgb(0, 0, 0))" }} />
                {foodItems.length > 0 ? foodItems.filter(
                  (item) => item.CategoryName === data.CategoryName && item.name.toLowerCase().includes(search.toLowerCase())
                ).map(filterItems => {
                  return (
                    <div key={filterItems.id} className='col-12 col-md-6 col-lg-3'>
                      <Card foodName={filterItems.name} item={filterItems} options={filterItems.options[0]} ImgSrc={filterItems.img} />
                    </div>
                  );
                }) : <div>No Such Data</div>}
              </div>
            );
          })
        ) : (
          <div>No Categories Available</div>
        )}
      </div>
      <Footer />
    </div>
  );
}
