import React, { useState } from "react";

const AddFoodItem = () => {
  const [foodItem, setFoodItem] = useState({
    name: "",
    price: "",
    image: null
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("name", foodItem.name);
    formData.append("price", foodItem.price);
    formData.append("image", foodItem.image);

    const response = await fetch("http://localhost:5000/api/admin/add-food-item", {
      method: "POST",
      headers: {
        Authorization: `Bearer ${localStorage.getItem("authToken")}`,
      },
      body: formData,
    });

    const data = await response.json();
    if (data.success) {
      alert("Food item added successfully");
    } else {
      alert("Error adding food item");
    }
  };

  return (
    <div>
      <h2>Add Food Item</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Food Name"
          value={foodItem.name}
          onChange={(e) => setFoodItem({ ...foodItem, name: e.target.value })}
        />
        <input
          type="number"
          placeholder="Price"
          value={foodItem.price}
          onChange={(e) => setFoodItem({ ...foodItem, price: e.target.value })}
        />
        <input
          type="file"
          onChange={(e) => setFoodItem({ ...foodItem, image: e.target.files[0] })}
        />
        <button type="submit">Add Food Item</button>
      </form>
    </div>
  );
};

export default AddFoodItem;
