import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from "react-router-dom";
import Badge from "@mui/material/Badge";
import ShoppingCartIcon from "@mui/icons-material/ShoppingCart";
import { useCart } from '../components/ContextReducer';
import Modal from '../Modal';
import Cart from '../screens/Cart';

export default function Navbar(props) {
    const [cartView, setCartView] = useState(false);
    const [isAuthenticated, setIsAuthenticated] = useState(!!localStorage.getItem("authToken"));
    const [profileImage, setProfileImage] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        // Set up a listener to detect changes to the authToken in localStorage
        const handleAuthChange = () => {
            setIsAuthenticated(!!localStorage.getItem("authToken"));
            // Retrieve the profile image from localStorage (or API if available)
            const userProfile = JSON.parse(localStorage.getItem('userProfile'));
            if (userProfile && userProfile.profilePicture) {
                setProfileImage(userProfile.profilePicture);
            }
        };

        // Add an event listener for storage changes (for auth state)
        window.addEventListener('storage', handleAuthChange);

        // Clean up the event listener when the component is unmounted
        return () => {
            window.removeEventListener('storage', handleAuthChange);
        };
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('authToken');
        setIsAuthenticated(false); // Update the state to reflect the logout
        navigate("/signup"); // Redirect to the signup page after logout
    };

    const loadCart = () => {
        setCartView(true);
    };

    const items = useCart();

    return (
        <div>
            <nav className="navbar navbar-expand-lg navbar-dark bg-success position-sticky"
                style={{ boxShadow: "0px 10px 20px black", position: "fixed", zIndex: "10", width: "100%" }}>
                <div className="container-fluid">
                    <Link className="navbar-brand fs-1 fst-italic" to="/">GoFood</Link>
                    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className="collapse navbar-collapse" id="navbarSupportedContent">
                        <ul className="navbar-nav me-auto mb-2 mb-lg-0">
                            <li className="nav-item">
                                <Link className="nav-link fs-5 mx-3 active" aria-current="page" to="/">Home</Link>
                            </li>
                            {isAuthenticated && (
                                <li className="nav-item">
                                    <Link className="nav-link fs-5 mx-3 active" aria-current="page" to="/MyOrder">My Orders</Link>
                                </li>
                            )}
                        </ul>
                        {!isAuthenticated ? (
                            <div className="d-flex">
                                <Link className="btn bg-white text-success mx-1" to="/login">Login</Link>
                                <Link className="btn bg-white text-success mx-1" to="/signup">Signup</Link>
                                <Link className="btn bg-white text-success mx-1" to="/ProfileDashboard">Profile DashBoard</Link>
                            </div>
                        ) : (
                            <div>
                                <div className="btn bg-white text-success mx-2">
                                    {profileImage && (
                                        <img
                                            src={`http://localhost:5000${profileImage}`}
                                            alt="Profile"
                                            style={{
                                                width: '40px',
                                                height: '40px',
                                                borderRadius: '50%', // Circular style
                                            }}
                                        />
                                    )}
                                    <button className="btn bg-white text-success" onClick={handleLogout}>
                                        Logout
                                    </button>
                                </div>
                                <Badge badgeContent={items.length} color="error">
                                    <ShoppingCartIcon onClick={loadCart} />
                                </Badge>
                            </div>
                        )}
                    </div>
                </div>
            </nav>
            {cartView && (
                <Modal closeCart={() => setCartView(false)}>
                    <Cart />
                </Modal>
            )}
        </div>
    );
}
