import React, { useState, useRef, useEffect } from 'react';
import { useDispatchCart } from '../components/ContextReducer';

export default function Card(props) {
  const dispatch = useDispatchCart();
  const [qty, setQty] = useState(1);
  const [size, setSize] = useState('');
  const priceRef = useRef();

  const options = props.options || {}; // Ensure options is always an object
  const priceOptions = Object.keys(options);

  // Log props to see what is passed down
  useEffect(() => {
    console.log('Food Item Props:', props);
    console.log('Options:', options);
  }, [props]);

  // Set default size when options are available
  useEffect(() => {
    if (priceOptions.length > 0 && !size) {
      setSize(priceOptions[0]); // Set the default size if no size is selected
      console.log('Setting default size:', priceOptions[0]); // Debug log
    }
  }, [priceOptions, size]);

  // Handle the change in quantity
  const handleQty = (e) => {
    setQty(e.target.value);
  };

  // Handle the change in selected size
  const handleOptions = (e) => {
    setSize(e.target.value);
    console.log('Selected Size:', e.target.value); // Debug log
  };

  // Add the item to the cart
  const handleAddtoCart = () => {
    if (size) {
      const finalPrice = qty * parseInt(options[size] || 0);
      dispatch({
        type: 'ADD',
        id: props.foodItem._id,
        name: props.foodItem.name,
        price: finalPrice,
        qty: qty,
        size: size,
      });
    } else {
      alert('Please select a size.');
    }
  };

  // Calculate the price based on the selected size and quantity
  const finalPrice = qty * (parseInt(options[size] || 0));

  return (
    <div>
      <div className="card mt-3 bg-dark text-light" style={{ width: '18rem', maxHeight: '400px' }}>
        <img
          src={props.foodItem.img}
          className="card-img-top"
          alt={props.foodItem.name}
          style={{ height: '120px', objectFit: 'cover' }}
        />
        <div className="card-body">
          <h5 className="card-title">{props.foodItem.name}</h5>
          <div className="container w-100 p-0" style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            {/* Quantity Dropdown */}
            <select
              className="m-2 h-100 w-25 bg-success text-white rounded"
              onChange={handleQty}
              value={qty}
            >
              {Array.from(Array(6), (e, i) => (
                <option key={i + 1} value={i + 1}>
                  {i + 1}
                </option>
              ))}
            </select>

            {/* Size Options Dropdown */}
            <select
              className="m-2 h-100 w-25 bg-success text-white rounded"
              ref={priceRef}
              onChange={handleOptions}
              value={size}
              disabled={priceOptions.length === 0} // Disable size dropdown if no options
            >
              {priceOptions.length > 0 ? (
                priceOptions.map((option) => (
                  <option key={option} value={option}>
                    {option}
                  </option>
                ))
              ) : (
                <option value="">No Size Available</option>
              )}
            </select>

            {/* Price Display */}
            <div className="d-inline ms-2 h-100 w-25 fs-5 text-white text-center">
              ₹{finalPrice || 0}/-
            </div>
          </div>

          <hr className="bg-secondary" />
          <button className="btn btn-success w-100" onClick={handleAddtoCart}>
            Add to Cart
          </button>
        </div>
      </div>
    </div>
  );
}
