import React, { useState } from 'react';

const Footer = () => {
  const [userId, setUserId] = useState('');
  const [foodItemId, setFoodItemId] = useState('');
  const [rating, setRating] = useState('');
  const [review, setReview] = useState('');
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  const handleCreate = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/reviews', { // Updated URL
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ userId, foodItemId, rating, review }),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const data = await response.json();
      setSuccess('Review created successfully!');
      setError(null);
      // Clear form fields if needed
      setUserId('');
      setFoodItemId('');
      setRating('');
      setReview('');
    } catch (error) {
      setError(`Error creating review: ${error.message}`);
      setSuccess(null);
    }
  };

  return (
    <div>
      <h2>Submit a Review</h2>
      <input
        type="text"
        placeholder="User ID"
        value={userId}
        onChange={(e) => setUserId(e.target.value)}
      />
      <input
        type="text"
        placeholder="Food Item ID"
        value={foodItemId}
        onChange={(e) => setFoodItemId(e.target.value)}
      />
      <input
        type="number"
        placeholder="Rating (1-5)"
        value={rating}
        onChange={(e) => setRating(e.target.value)}
      />
      <textarea
        placeholder="Review"
        value={review}
        onChange={(e) => setReview(e.target.value)}
      />
      <button onClick={handleCreate}>Submit Review</button>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {success && <p style={{ color: 'green' }}>{success}</p>}
    </div>
  );
};

export default Footer;
