import React, { useEffect, useState } from 'react';

function Footer() {
  const [reviews, setReviews] = useState([]);
  const [newReview, setNewReview] = useState({ user: '', comment: '', rating: 1 });
  const [editReview, setEditReview] = useState(null);

  // Fetch reviews from the backend
  const fetchReviews = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/reviews');
      const data = await response.json();
      setReviews(data);
    } catch (error) {
      console.error('Error fetching reviews:', error);
    }
  };

  useEffect(() => {
    fetchReviews();
  }, []);

  // Create a new review
  const handleCreate = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/reviews', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newReview),
      });
      const data = await response.json();
      setReviews([...reviews, data]);
      setNewReview({ user: '', comment: '', rating: 1 });
    } catch (error) {
      console.error('Error creating review:', error);
    }
  };

  // Update an existing review
  const handleUpdate = async () => {
    if (!editReview) return;

    try {
      const response = await fetch(`http://localhost:5000/api/reviews/${editReview._id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(editReview),
      });
      const data = await response.json();
      setReviews(reviews.map(review => (review._id === data._id ? data : review)));
      setEditReview(null);
    } catch (error) {
      console.error('Error updating review:', error);
    }
  };

  // Delete a review
  const handleDelete = async (id) => {
    try {
      await fetch(`http://localhost:5000/api/reviews/${id}`, { method: 'DELETE' });
      setReviews(reviews.filter(review => review._id !== id));
    } catch (error) {
      console.error('Error deleting review:', error);
    }
  };

  return (
    <footer className="bg-dark text-light py-3">
      <div className="container">
        <h4>User Reviews</h4>
        <ul className="list-unstyled">
          {reviews.map(review => (
            <li key={review._id}>
              <div>
                <strong>{review.user}</strong>: {review.comment} (Rating: {review.rating})
                <button onClick={() => handleDelete(review._id)} className="btn btn-danger btn-sm ms-2">Delete</button>
                <button onClick={() => setEditReview(review)} className="btn btn-primary btn-sm ms-2">Edit</button>
              </div>
            </li>
          ))}
        </ul>
        {editReview ? (
          <div>
            <h5>Edit Review</h5>
            <input
              type="text"
              value={editReview.user}
              onChange={(e) => setEditReview({ ...editReview, user: e.target.value })}
              placeholder="User"
            />
            <input
              type="text"
              value={editReview.comment}
              onChange={(e) => setEditReview({ ...editReview, comment: e.target.value })}
              placeholder="Comment"
            />
            <input
              type="number"
              min="1"
              max="5"
              value={editReview.rating}
              onChange={(e) => setEditReview({ ...editReview, rating: e.target.value })}
              placeholder="Rating"
            />
            <button onClick={handleUpdate} className="btn btn-warning">Update</button>
          </div>
        ) : (
          <div>
            <h5>Add Review</h5>
            <input
              type="text"
              value={newReview.user}
              onChange={(e) => setNewReview({ ...newReview, user: e.target.value })}
              placeholder="User"
            />
            <input
              type="text"
              value={newReview.comment}
              onChange={(e) => setNewReview({ ...newReview, comment: e.target.value })}
              placeholder="Comment"
            />
            <input
              type="number"
              min="1"
              max="5"
              value={newReview.rating}
              onChange={(e) => setNewReview({ ...newReview, rating: e.target.value })}
              placeholder="Rating"
            />
            <button onClick={handleCreate} className="btn btn-success">Add Review</button>
          </div>
        )}
      </div>
    </footer>
  );
}

export default Footer;
